import React, {useEffect, useState} from 'react'
import './css/center_menu.css'
import 'pure-react-carousel/dist/react-carousel.es.css';
import image from "../../img/l1.webp"; 
import img from "../../img/r1.webp"; 

import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Center_menu = () => {

    const [nav1, setNav1] = useState(null);
    const [nav2, setNav2] = useState(null);
    const [slider1, setSlider1] = useState(null);
    const [slider2, setSlider2] = useState(null);


  const settingsMain = {
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplaySpeed : 9000,
    arrows: false,
    fade: true,
    autoplay: true,

  };

  const settingsThumbs = {
    slidesToShow: 4,
    slidesToScroll: 1,
    asNavFor: '.slider-for',
    centerMode: true,
    swipeToSlide: true,
    focusOnSelect: true,
    centerPadding: '10px',
    cssEase: 'in-out' == 'cubic-bezier(0.250, 0.250, 0.750, 0.750)'
  };
  useEffect(() => {
    setNav1(slider1);
    setNav2(slider2);

  });
  const slidesData = [
    {
      id: 1,
      title: 'repellendus id ullam',
      img: '../../img/l1.webp',
      label: 'Dolorem officiis temporibus. 0'
    }, {
      id: 2,
      title: 'excepturi consequatur est',
      label: 'Officia non provident dolor esse et neque.'
    }, {
      id: 3,
      title: 'eius doloribus blanditiis',
      label: 'Ut recusandae vel vitae molestiae id soluta.'
    }, {
      id: 4,
      title: 'nihil voluptates delectus',
      label: 'Qui vel consequatur recusandae illo repellendus.'
    }, {
      id: 5,
      title: 'nemo dolorem necessitatibus',
      label: 'Placeat odit velit itaque voluptatem.'
    }, {
      id: 6,
      title: 'dolorem quibusdam quasi',
      label: 'Adipisci officiis repudiandae.'
    },
  ];
  return (


    <div className="App">

      <div className="slider-wrapper">

        <Slider
          {...settingsMain}
          asNavFor={nav2}
          ref={slider => (setSlider1(slider))}
        >

          {slidesData.map((slide) =>

            <div className="slick-slide" key={slide.id}>
              {/* <h2 className="slick-slide-title">{slide.title}</h2> */}
              <img className="slick-slide-image" src={slide.img} alt=''/>
              {/* <label className="slick-slide-label">{slide.label}</label> */}
            </div>

          )}

        </Slider>
        <div className="thumbnail-slider-wrap">
          <Slider
            {...settingsThumbs}
            asNavFor={nav1}
            ref={slider => (setSlider2(slider))}>

            {slidesData.map((slide) =>

              <div className="slick-slide01" key={slide.id}>
                {/* <img className="slick-slide-image" src={`https://picsum.photos/800/400?img=${slide.id}`} /> */}
                <label className="slick-slide-label">{slide.label}</label>
              </div>

            )}

          </Slider>
        </div>
      </div>

    </div>

  )
}

export default Center_menu
