import React, {useEffect, useState} from 'react'
import './css/center_menu.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

const Center_menu = () => {

  const Data = [
    {
        id: 1,
        title: 'repellendus id ullam',
        img: '../../img/l1.webp',
        label: 'Dolorem officiis temporibus. 0'
    },
    {
        id: 2,
        title: 'repellendus id ullam',
        img: '../../img/r1.webp',
        label: 'Dolorem officiis temporibus. 0'
    },
    {
        id: 3,
        title: 'repellendus id ullam',
        img: '../.././img/r1.webp',
        label: 'Dolorem officiis temporibus. 0'
    },
    {
        id: 4,
        title: 'repellendus id ullam',
        img: '../../img/l1.webp',
        label: 'Dolorem officiis temporibus. 0'
    },
];


  return (
    <div className="main">
    <div style={{ margin: "30px" }} className="carousel">
      <h1>Basic carousel</h1>
      <Slider
        autoplay
        autoplaySpeed={2000}
        dots
        initialSlide={2}
        fadeSpeed= {1000}
        infinite
        // customPaging={(i) => {
        //   return (
        //     <div>
        //     {i === Data.label}
        //     </div>
        //   );
        // }}
        pauseOnHover
        customPaging = {(i) => {
          return(
            <div

                style={{
                    backgroundColor: "#ddd",
                    borderRadius: "10px",
                    marginTop: "10px",
                    padding: "20px"
                }}
            >
                <ul style={{ margin: "0px" }}> {{i}}</ul>
            </div>
            );}
        }
        dotsClass="slick-dots custom-indicator"
      >
        {Data.map((item) => (
          <div>
            <img src={item.img} alt="" style={{ width: "100%", height: "40vh" }} />
          </div>
        ))}
      </Slider>
  </div>
</div>
  )
}

export default Center_menu
