/* eslint-disable no-undef */

import React, {useEffect, useState} from 'react'
import './css/center_menu.css'
import 'pure-react-carousel/dist/react-carousel.es.css';
import image from "../../img/l1.webp"; 
import img from "../../img/r1.webp"; 

import Slider from "react-slick";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Center_menu = () => {

    const [nav1, setNav1] = useState(null);
    const [nav2, setNav2] = useState(null);
    const [slider1, setSlider1] = useState(null);
    const [slider2, setSlider2] = useState(null);


  const settingsMain = {
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplaySpeed : 9000,
    arrows: false,
    fade: true,
    autoplay: true,

  };

  const settingsThumbs = {
    slidesToShow: 3,
    slidesToScroll: 1,
    asNavFor: '.slider-for',
    centerMode: true,
    swipeToSlide: true,
    focusOnSelect: true,
    centerPadding: '10px',
  };
  useEffect(() => {
    setNav1(slider1);
    setNav2(slider2);
    if ( $('.product__slider-main').length ) {
        var $slider = $('.product__slider-main')
            .on('init', function(slick) {
                $('.product__slider-main').fadeIn(1000);
            })
            .slick({
                slidesToShow: 1,
                slidesToScroll: 1,
                arrows: true,
                autoplay: true,
                lazyLoad: 'ondemand',
                autoplaySpeed: 3000,
                asNavFor: '.product__slider-thmb'
            });

    var $slider2 = $('.product__slider-thmb')
            .on('init', function(slick) {
                $('.product__slider-thmb').fadeIn(1000);
            })
            .slick({
                slidesToShow: 4,
                slidesToScroll: 1,
                lazyLoad: 'ondemand',
                asNavFor: '.product__slider-main',
                dots: false,
                centerMode: false,
                focusOnSelect: true
            });

 //remove active class from all thumbnail slides
 $('.product__slider-thmb .slick-slide').removeClass('slick-active');

 //set active class to first thumbnail slides
 $('.product__slider-thmb .slick-slide').eq(0).addClass('slick-active');

 // On before slide change match active thumbnail to current slide
 $('.product__slider-main').on('beforeChange', function (event, slick, currentSlide, nextSlide) {
 	var mySlideNumber = nextSlide;
 	$('.product__slider-thmb .slick-slide').removeClass('slick-active');
 	$('.product__slider-thmb .slick-slide').eq(mySlideNumber).addClass('slick-active');
});


  // init slider
require(['js-sliderWithProgressbar'], function(slider) {

    $('.product__slider-main').each(function() {

        me.slider = new slider($(this), options, sliderOptions, previewSliderOptions);

        // stop slider
        //me.slider.stop();

        // start slider
        //me.slider.start(index);

        // get reference to slick slider
        //me.slider.getSlick();

    });
});
  var options = {
    progressbarSelector    : '.bJS_progressbar'
    , slideSelector        : '.bJS_slider'
    , previewSlideSelector : '.bJS_previewSlider'
    , progressInterval     : ''
        // add your own progressbar animation function to sync it i.e. with a video
        // function will be called if the current preview slider item (".b_previewItem") has the data-customprogressbar="true" property set
    , onCustomProgressbar : function($slide, $progressbar) {}
}

    // slick slider options
    // see: https://kenwheeler.github.io/slick/
var sliderOptions = {
    slidesToShow   : 1,
    slidesToScroll : 1,
    arrows         : false,
    fade           : true,
    autoplay       : true
}

    // slick slider options
    // see: https://kenwheeler.github.io/slick/
var previewSliderOptions = {
    slidesToShow   : 3,
    slidesToScroll : 1,
    dots           : false,
    focusOnSelect  : true,
    centerMode     : true
}
}

  });
  const slidesData = [
    {
      id: 1,
      title: 'repellendus id ullam',
      img: '../../img/l1.webp',
      label: 'Dolorem officiis temporibus. 0'
    }, {
      id: 2,
      title: 'excepturi consequatur est',
      label: 'Officia non provident dolor esse et neque.'
    }, {
      id: 3,
      title: 'eius doloribus blanditiis',
      label: 'Ut recusandae vel vitae molestiae id soluta.'
    }, {
      id: 4,
      title: 'nihil voluptates delectus',
      label: 'Qui vel consequatur recusandae illo repellendus.'
    }, {
      id: 5,
      title: 'nemo dolorem necessitatibus',
      label: 'Placeat odit velit itaque voluptatem.'
    }, {
      id: 6,
      title: 'dolorem quibusdam quasi',
      label: 'Adipisci officiis repudiandae.'
    },
  ];
  return (


    <div className="App">

      <div className="slider-wrapper">

        <Slider
          {...settingsMain}
          asNavFor={nav2}
          ref={slider => (setSlider1(slider))}
        >

          {slidesData.map((slide) =>

            <div className="slick-slide" key={slide.id}>
              {/* <h2 className="slick-slide-title">{slide.title}</h2> */}
              <img className="slick-slide-image" src={slide.img} alt=''/>
              {/* <label className="slick-slide-label">{slide.label}</label> */}
            </div>

          )}

        </Slider>
        <div className="thumbnail-slider-wrap">
          <Slider
            {...settingsThumbs}
            asNavFor={nav1}
            ref={slider => (setSlider2(slider))}>

            {slidesData.map((slide) =>

              <div className="slick-slide01" key={slide.id}>
                {/* <img className="slick-slide-image" src={`https://picsum.photos/800/400?img=${slide.id}`} /> */}
                <label className="slick-slide-label">{slide.label}</label>
              </div>

            )}

          </Slider>
        </div>
      </div>
{/*  */}
<div class="wrapper">
<div class="container">
<div class="row">
  <div class="col-xs-12">
    <div id="product__slider">
      <div class="product__slider-main">
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
      </div>
      <div class="product__slider-thmb">
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
        <div class="slide"><img src="http://dummyimage.com/300" alt="" class="img-responsive"/></div>
      </div>
    </div>
  </div>
</div>
</div>

  </div>

    </div>

  )
}

export default Center_menu
