    const settings =  {
    //   arrowsScroll: 2,
      autoplay: true,
    //   initialSlide: true,
    //   rows: 2,
    //   slidesToShow: 5,
      swipe: false,
    // slidesToScroll: 1,
    // arrows: false,
    // infinite: true,
    // autoplaySpeed : 9000,
    // centerMode: true,
    // swipeToSlide: true,
    // focusOnSelect: true,
    centerPadding: '10px',
    };
    const slidesData = [
    {
        id: 1,
        text: 'text',

    },
    {
        id: 2,
        text: 'text',

    },
    {
        id: 3,
        text: 'text',
    },
    {
        id: 4,
        text: 'text',
    }
];
  return (
    <div>
        <span>CustomSlider</span>
        <Slider { ...settings }>
        <div>
            <Link >
                {/* <img className='right-banner__img' /> */}
                {slidesData.map((slide) =>
            <div >
            {slide.text}
              {/* <img className='' src={slide.img} alt=''/> */}
            </div>
            )}
            </Link>
            </div>
        </Slider>
      </div>
  )
}

export default Sanphamnoibat
