import React from 'react'
import './css/sanphamdienthoai.css'
import {Link  } from 'react-router-dom';
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

import LeftArrow from "../../img/left-curved-arrow.png"
import RightArrow from "../../img/left-curved.png"

const Sanphamdienthoai = () => {
  const Data = [
    {
      id_danhmuc: 1,
      id_tensanpham: '1',
      danhmuc: 'Apple',
    },
    {
      id_danhmuc: 2,
      id_tensanpham: '1',
      danhmuc: 'Samsung',
    },
    {
      id_danhmuc: 3,
      id_tensanpham: '1',
      danhmuc: 'Xiaomi',
    },
    {
      id_danhmuc: 4,
      id_tensanpham: '1',
      danhmuc: 'OPPO',
    },
    {
      id_danhmuc: 5,
      id_tensanpham: '1',
      danhmuc: 'Vivo',
    },
    {
      id_danhmuc: 6,
      id_tensanpham: '1',
      danhmuc: 'realme',
    },
    {
      id_danhmuc: 7,
      id_tensanpham: '1',
      danhmuc: 'Nokia',
    },
    {
      id_danhmuc: 8,
      id_tensanpham: '1',
      danhmuc: 'ASUS',
    },
    {
      id_danhmuc: 9,
      id_tensanpham: '1',
      danhmuc: 'Tecno',
    }
  ]
  const SlickArrowLeft = ({ currentSlide, slideCount, ...props }) => (
    <img src={LeftArrow} alt="prevArrow" {...props} className="SlickArrowLeft"/>
  );

  const SlickArrowRight = ({ currentSlide, slideCount, ...props }) => (
    <img src={RightArrow} alt="nextArrow" {...props} className="SlickArrowRight"/>
  );
    const sliderSettings =  {
      initialSlide: 0,
      autoplay: true,
      centerMode: false,
      swipeToSlide: true,
      pauseOnDotsHover: true,
      arrows: true,
      rows: 2,
      draggable: true,
      slidesToShow: 5,
      shift: 10,
      dots: false,
      infinite: true,
      wheel: true,
      speed: 500,
      prevArrow: <SlickArrowLeft />,
    nextArrow: <SlickArrowRight />,

    };
  return (
    <div>

      <div className='sanphamdienthoai_danhmuc'>
      <div className='sanphamdienthoai_danhmuc_tensanpham'>ĐIỆN THOẠI NỔI BẬT NHẤT</div>
      <div className='block_featured_product_dienthoai'>
        {Data.map((slide) =>
        <Link>
          <div className='sanphamdienthoai_danhmuc_loaidanhmuc'>{slide.danhmuc}</div></Link>
        )}
        <Link>
        <div className='sanphamdienthoai_danhmuc_loaidanhmuc'>Xem tất cả</div></Link>
      </div>
      </div>
      <div className='sanphamdienthoai_dienthoai'>

      </div>
    </div>
  )
}

export default Sanphamdienthoai
