import React, {useState, useEffect} from 'react';
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import { Link  } from 'react-router-dom';

import Left_Laptop from "../../img/left-curved-arrow.png"
import Right_Laptop from "../../img/left-curved.png"
import Axios from 'axios';

import img from "../../img/download.svg"; 
import './css/phukien.css'
const Phukien = () => {
    const Data = [
        {
          id_danhmuc: 1,
          id_tensanpham: '1',
          danhmuc: 'Máy tính bàn',
        },
        {
          id_danhmuc: 2,
          id_tensanpham: '1',
          danhmuc: 'PC đồng bộ',
        },
        {
          id_danhmuc: 3,
          id_tensanpham: '1',
          danhmuc: 'PC Gaming',
        },
        {
          id_danhmuc: 4,
          id_tensanpham: '1',
          danhmuc: 'PC Đồ họa',
        },
        {
          id_danhmuc: 5,
          id_tensanpham: '1',
          danhmuc: 'Build PC',
        },
        {
          id_danhmuc: 6,
          id_tensanpham: '1',
          danhmuc: 'Màn hình văn phòng',
        }
      ]

      const [data, setData] = useState([]);

      useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await Axios.get(`http://localhost:3005/`);
                setData(res.data);
            }catch (err){
                console.log(err);
            }
        };
        fetchData();
    }, [])
  return (
    <div className='phukien'>
        <div className='sanphammanhinh_danhmuc'>
      <div className='sanphammanhinh_danhmuc_tensanpham'>ĐỒNG HỒ THÔNG MINH</div>
      <div className='block_featured_product_manhinh'>
        {Data.map((slide) =>
        <Link>
          <div className='sanphammanhinh_danhmuc_loaidanhmuc'>{slide.danhmuc}</div></Link>
        )}
        <Link>
        <div className='sanphammanhinh_danhmuc_loaidanhmuc'>Xem tất cả</div></Link>
      </div>
      </div>
      <div className='sanpham'>
      <Slider {...sliderSettings_dienthoai}>
      {data.map((card) => {
        const formatter = new Intl.NumberFormat('vi', {
        style: 'currency',
        currency: 'VND',
        minimumFractionDigits: 2
        })
      return( 

        <div key="{index}" className="block-featured-product">
      <Link >
      <div className='product_price--percent' style={{ backgroundImage:`url(${img})`, backgroundRepeat:"no-repeat", backgroundSize:"contain"}}><p>Giảm 19%</p></div>
      <img className="img01" alt={card.title} src={`./dienthoai/anhdong/${card.hinhanh}`}/>
      <div className="block-featured-product-body">
        <div className="block-featured-title"><h3>{card.tensanpham}</h3></div>
        <div className="block-featured-text">{formatter.format(card.gia)}</div>
        <div>⭐⭐⭐⭐⭐</div>
        <div className='uudai'>
        { card.title === undefined && <div className="block-featured-text_uudai">{card.tensanpham}{card.tensanpham}</div>}
        </div>
      </div>
      </Link>
      <div className='yeuthich'> <p>Yêu thích <i class="fa fa-heart-o"></i></p></div>
    </div>

    )})}
</Slider>
      </div>
    </div>
  )
}

export default Phukien
